import ReportComponent from "./component/ReportComponent";
// import { v4 as uuidv4 } from 'uuid';
import './App.css';
import React, { useState, useEffect } from "react";

function App() {
  const initData = [
    {id:1, title: "ค่ารักษาพยาบาล", amount: -1000},
    {id:2, title: "ค่าอาหาร", amount: -200},
