import Transection from "./component/Transection";
import FormComponent from "./component/FormComponent";
import DataContext from "./data/DataContext";
import ReportComponent from "./component/ReportComponent";
// import { v4 as uuidv4 } from 'uuid';
import './App.css';
import React, { useState, useEffect, useReducer } from "react";

function App() {
  const initData = [
    {id:1, title: "ค่ารักษาพยาบาล", amount: -1000},
    {id:2, title: "ค่าอาหาร", amount: -200},
    {id:3, title: "ค่าเดินทาง", amount: -300},
    {id:4, title: "ขายของออนไลน์", amount: 3000}
  ]

  const [items, setItems] = useState(initData)
  const [reportIncome, setReportIncome] = useState(0)
  const [reportExpense, setReportExpense] = useState(0)
  const onAddNewItem = (newItem) => {
    setItems((prevItem)=>{
      return [newItem, ...prevItem]
    })
  }
  useEffect(()=>{
    const amounts = items.map((item)=>item.amount)
    const income = amounts.filter((element)=>element>0).reduce((total, element)=>total+=element,0)
    const expense = (amounts.filter((element)=>element<0).reduce((total, element)=>total+=element,0)) * -1
    setReportIncome(income)
    setReportExpense(expense)
  }, [items, reportIncome, reportExpense])

  //Reducer State
  const [count, setCount] = useState(0)
  const reducer = (state, action)=>{
    switch(action.type){
      case "ADD" :
        return state+1
      case "SUB" :
        return state-1
      case "CLEAR" :
        return 0
    }
  }

  const [result, dispatch] = useReducer(reducer,count)
  return (
    // <div className="App">
    //   <h1>รายการรายรับ-รายจ่าย</h1>
    //   <FormComponent onAddItem={onAddNewItem}/>
    //   <Transection items = {items}/>
    // </div>
    // <DataContext.Provider value={
    //   {
    //     income : reportIncome,
    //     expense : reportExpense,
    //   }
    // }>
    //   <div className="App">
    //     <h1>รายการรายรับ-รายจ่าย</h1>
    //     <ReportComponent/>
    //     <FormComponent onAddItem={onAddNewItem}/>
    //     <Transection items = {items}/>
    //   </div>
    // </DataContext.Provider>
    <div align='center'>
      <h1>{result}</h1>
      <button onClick={()=>dispatch({type:"ADD"})}>เพิ่มข้อมูล</button>
      <button onClick={()=>dispatch({type:"SUB"})}>ลดข้อมูล</button>
      <button onClick={()=>dispatch({type:"CLEAR"})}>ล้างข้อมูล</button>
    </div>
  );
}

export default App;
